mapper.select:
select pro_num, cate_code, pro_name, pro_price, pro_discount, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate
from (
select /*+ index_desc(product_t pk_pro_num) */
rownum as rn, pro_num, cate_code, pro_name, pro_price, pro_discount, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate
from product_t
where cate_code = #{cate_code}
and

		   		ROWNUM <= (#{cri.pageNum} * #{cri.amount})   -- 페이지번호 * 페이지별 출력건수

)
where rn > (#{cri.pageNum} - 1 ) * #{cri.amount}

select count(*)
from product_t
where cate_code = #{cate_code}

select pro_num, cate_code, pro_name, pro_price, pro_discount, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate
from product_t
where pro_num = #{pro_num}
