mapper.select:
select q_num, q_title, q_content, q_regdate, q_updatedate from( select /*+ index_desc(qna_t pk_q_num) */ rownum as rn, q_num, q_title, q_content, q_regdate, q_updatedate from qna_t where --페이지 번호 * 페이지별 출력건수
rownum <= (#{pageNum} * #{amount})
) where rn > (#{pageNum} - 1) * #{amount} --(페이지번호 - 1) * 페이지별 출력건수

select q_num, q_title, q_content, q_regdate, q_updatedate
from qna_t
where q_num = #{q_num}

mapper.update:
update qna_t
set q_title = #{q_title}, q_content = #{q_content}, q_updatedate = sysdate
where q_num = #{q_num}
