mapper.update:
update mailing_t
set m_send_count = m_send_count + 1
where m_idx = #{idx}

mapper.select:
select m_idx, m_title, m_content, m_gubun, m_send_count, reg_date
from(
select
@rownum := @rownum + 1 as rn, m_idx, m_title, m_content, m_gubun, m_send_count, reg_date
from mailing_t, (select @rownum := 0) as rowcolumn order by m_idx desc) as rownum_table
where 1 = 1

and

rn <= (#{cri.pageNum} * #{cri.amount})

and 
rn > (#{cri.pageNum} - 1 ) * #{cri.amount}

select m_idx, m_title, m_content, m_gubun, m_send_count, reg_date
from mailing_t
where m_idx = #{idx}

mapper.update:
update mailing_t
set m_title = #{title}, m_content = #{content}, m_gubun = #{gubun}, reg_date = now()
where m_idx = #{idx}

mapper.select:
select u_id, u_name, u_email, u_zipcode, u_addr, u_deaddr, u_phone, u_nick, u_receive, u_point, u_lastlogin, u_regdate, u_update from( select @rownum := @rownum + 1 as rn, u_id, u_name, u_email, u_zipcode, u_addr, u_deaddr, u_phone, u_nick, u_receive, u_point, u_lastlogin, u_regdate, u_update from user_t, (select @rownum := 0) as rowcolumn order by u_id desc) as rownum_table where
rn <= (#{pageNum} * #{amount})
and rn > (#{pageNum} - 1) * #{amount}
