mapper.select:
select pro_num, cate_code, pro_name, pro_price, pro_discount, pro_disprice, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate
from(
select
@rownum := @rownum + 1 as rn, pro_num, cate_code, pro_name, pro_price, pro_discount, pro_disprice, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate
from product_t, (select @rownum := 0) as rowcolumn order by pro_num desc) as rownum_table
where cate_code = #{cate_code}
and

rn <= (#{cri.pageNum} * #{cri.amount})

and 
rn > (#{cri.pageNum} - 1 ) * #{cri.amount}

select count(*)
from product_t
where cate_code = #{cate_code}

select pro_num, cate_code, pro_name, pro_price, pro_discount, pro_disprice, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate
from product_t
where pro_num = #{pro_num}

select pro_num, cate_code, pro_name, pro_price, pro_discount, pro_disprice, pro_publisher, pro_content, pro_up_folder, pro_img, pro_amount, pro_buy, pro_date, pro_updatedate
from product_t
where pro_num = #{pro_num}
