mapper.select:
select u_id 
from user_t
where u_id = #{u_id}

mapper.update:
update user_t
set u_lastlogin = sysdate
where u_id = #{u_id}

mapper.select:
select u_id
from user_t
where u_name = #{u_name}
and u_email = #{u_email}

select u_email
from user_t
where u_id = #{u_id}
and u_name = #{u_name}
and u_email = #{u_email}

mapper.update:
update user_t
set u_password = #{enc_pw}
where u_id = #{u_id}

update user_t
set u_email = #{u_email}, u_zipcode = #{u_zipcode}, u_addr = #{u_addr}, u_deaddr = #{u_deaddr}, u_phone = #{u_phone}, u_nick = #{u_nick}
where u_id = #{u_id}

update user_t
set u_password = #{new_pwd}
where u_id = #{u_id}

mapper.select:
select sns_login_type
from user_t
where u_email = #{sns_email}

select email
from sns_user_t
where email = #{sns_email}
