mapper.select:
select p_id, ord_code, u_id, kakao_id, naver_id, paymethod, p_disprice, p_status, payinfo, p_date
from payinfo
where ord_code = #{ord_code}

mapper.update:
update payinfo
set p_price = (
				select sum(dt_amount * dt_price) from ordt_t where ord_code = #{ord_code}
                )
where ord_code = #{ord_code}
